// SPDX-License-Identifier: GPL-3.0

// solhint-disable-next-line
pragma solidity ^0.8.0;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ContextUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {AddressUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import {StringsUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

import {IRenAsset} from "../RenAsset/interfaces/IRenAsset.sol";
import {GatewayStateV4, GatewayStateManagerV4} from "./common/GatewayState.sol";
import {RenVMHashes} from "./common/RenVMHashes.sol";
import {IMintGateway} from "./interfaces/IMintGateway.sol";
import {String} from "../libraries/String.sol";
import {CORRECT_SIGNATURE_RETURN_VALUE_} from "./RenVMSignatureVerifier.sol";

/// MintGateway handles verifying mint and burn requests. A mintAuthority
/// approves new assets to be minted by providing a digital signature. An owner
/// of an asset can request for it to be burnt.
contract MintGatewayV4 is Initializable, ContextUpgradeable, GatewayStateV4, GatewayStateManagerV4, IMintGateway {
    string public constant NAME = "MintGateway";

    event TokenOwnershipTransferred(address indexed tokenAddress, address indexed nextTokenOwner);

    // If these parameters are changed, RenAssetFactory must be updated as well.
    function __MintGateway_init(
        string calldata asset_,
        address signatureVerifier_,
        address token_,
        bool isNFT_
    ) external initializer {
        __Context_init();
        __GatewayStateManager_init(asset_, signatureVerifier_, token_, isNFT_);
    }

    // Governance functions ////////////////////////////////////////////////////

    /// @notice Allow the owner to update the owner of the RenERC20 token.
    function transferTokenOwnership(address nextTokenOwner) external onlySignatureVerifierOwner {
        require(AddressUpgradeable.isContract(nextTokenOwner), "MintGateway: next token owner must be a contract");
        require(nextTokenOwner != address(0x0), "MintGateway: invalid next token owner");

        address token_ = getToken();
        IRenAsset(token_).transferOwnership(address(nextTokenOwner));

        emit TokenOwnershipTransferred(token_, nextTokenOwner);
    }

    // PUBLIC FUNCTIONS ////////////////////////////////////////////////////////

    /// @notice mint verifies a mint approval signature from RenVM and creates
    ///         tokens after taking a fee for the `_feeRecipient`.
    ///
    /// @param pHash (payload hash) The hash of the payload associated with the
    ///        mint.
    /// @param amountOrTokenId The amount of the token being minted, in its smallest
    ///        value (e.g. satoshis for BTC) or the tokenID of the NFT.
    /// @param nHash (nonce hash) The hash of the nonce, amountOrTokenId and pHash.
    /// @param sig The signature of the hash of the following values:
    ///        (pHash, amountOrTokenId, recipient, nHash), signed by the mintAuthority.
    function mint(
        bytes32 pHash,
        uint256 amountOrTokenId,
        bytes32 nHash,
        bytes calldata sig
    ) external override returns (uint256) {
        return _mint(pHash, amountOrTokenId, nHash, sig, _msgSender());
    }

    /// @notice burnWithPayload allows minted assets to be released to their
    ///         native chain, or to another chain as specified by the chain and
    ///         payload parameters.
    ///         WARNING: Burning with invalid parameters can cause the funds to
    ///         become unrecoverable.
    ///
    /// @param recipientAddress The address to which the locked assets will be
    ///        minted to. The address should be a plain-text address, without
    ///        decoding to bytes first.
    /// @param recipientChain The target chain to which the assets are being
    ///        moved to.
    /// @param recipientPayload An optional payload to be passed to the
    ///        recipient chain along with the address.
    /// @param amountOrTokenId The amount of the token being minted, in its smallest
    ///        value (e.g. satoshis for BTC) or the tokenID of the NFT.
    function burnWithPayload(
        string calldata recipientAddress,
        string calldata recipientChain,
        bytes calldata recipientPayload,
        uint256 amountOrTokenId
    ) external override returns (uint256) {
        return _burnWithPayload(recipientAddress, recipientChain, recipientPayload, amountOrTokenId, _msgSender());
    }

    /// @notice burn is a convenience function that is equivalent to calling
    ///         `burnWithPayload` with an empty payload and chain, releasing
    ///         the asset to the native chain.
    function burn(string calldata recipient, uint256 amountOrTokenId) external virtual override returns (uint256) {
        return _burnWithPayload(recipient, "", "", amountOrTokenId, _msgSender());
    }

    /// Same as `burn` with the recipient parameter being `bytes` instead of
    /// a `string`. For backwards compatibility with the MintGatewayV2.
    function burn(bytes calldata recipient, uint256 amountOrTokenId) external virtual override returns (uint256) {
        return _burnWithPayload(string(recipient), "", "", amountOrTokenId, _msgSender());
    }

    function _mintFromPreviousGateway(
        bytes32 pHash,
        uint256 amountOrTokenId,
        bytes32 nHash,
        bytes calldata sig,
        address caller
    ) external onlyPreviousGateway returns (uint256) {
        return _mint(pHash, amountOrTokenId, nHash, sig, caller);
    }

    function _burnFromPreviousGateway(
        string calldata recipientAddress,
        string calldata recipientChain,
        bytes calldata recipientPayload,
        uint256 amountOrTokenId,
        address caller
    ) external onlyPreviousGateway returns (uint256) {
        return _burnWithPayload(string(recipientAddress), recipientChain, recipientPayload, amountOrTokenId, caller);
    }

    // INTERNAL FUNCTIONS //////////////////////////////////////////////////////

    function _mint(
        bytes32 pHash,
        uint256 amountOrTokenId,
        bytes32 nHash,
        bytes memory sig,
        address recipient
    ) internal returns (uint256) {
        // Calculate the hash signed by RenVM. This binds the payload hash,
        // amountOrTokenId, recipient and nonce hash to the signature.
        bytes32 sigHash = RenVMHashes.calculateSigHash(pHash, amountOrTokenId, getSelectorHash(), recipient, nHash);

        // Check that the signature hasn't been redeemed.
        require(!status(sigHash), "MintGateway: signature already spent");

        // If the signature fails verification, throw an error.
        // `isValidSignature` must return an exact bytes4 value, to avoid
        // a contract mistakingly returning a truthy value without intending to.
        if (getSignatureVerifier().isValidSignature(sigHash, sig) != CORRECT_SIGNATURE_RETURN_VALUE_) {
            revert(
                string(
                    abi.encodePacked(
                        "MintGateway: invalid signature. phash: ",
                        StringsUpgradeable.toHexString(uint256(pHash), 32),
                        ", amountOrTokenId: ",
                        StringsUpgradeable.toString(amountOrTokenId),
                        ", shash",
                        StringsUpgradeable.toHexString(uint256(getSelectorHash()), 32),
                        ", msg.sender: ",
                        StringsUpgradeable.toHexString(uint160(recipient), 20),
                        ", nhash: ",
                        StringsUpgradeable.toHexString(uint256(nHash), 32)
                    )
                )
            );
        }

        // Update the status for the signature hash.
        _status[sigHash] = true;

        // Mint the amountOrTokenId to the recipient.
        IRenAsset(getToken()).mint(recipient, amountOrTokenId);

        // Emit mint log. For backwards compatiblity reasons, the sigHash is
        // cast to a uint256.
        emit LogMint(recipient, amountOrTokenId, uint256(sigHash), nHash);

        return amountOrTokenId;
    }

    /// @notice burn destroys tokens after taking a fee for the `_feeRecipient`,
    ///         allowing the associated assets to be released on their native
    ///         chain.
    ///
    /// @param recipientAddress The address to which the locked assets will be
    ///        minted to. The address should be a plain-text address, without
    ///        decoding to bytes first.
    /// @param recipientChain The target chain to which the assets are being
    ///        moved to.
    /// @param recipientPayload An optional payload to be passed to the
    ///        recipient chain along with the address.
    /// @param amountOrTokenId The amount of the token being minted, in its smallest
    ///        value (e.g. satoshis for BTC) or the tokenID of the NFT.
    function _burnWithPayload(
        string memory recipientAddress,
        string memory recipientChain,
        bytes memory recipientPayload,
        uint256 amountOrTokenId,
        address caller
    ) internal returns (uint256) {
        // The recipient must not be empty. Better validation is possible,
        // but would need to be customized for each destination ledger.
        require(String.isNotEmpty(recipientAddress), "MintGateway: to address is empty");

        // Burn the tokens. If the user doesn't have enough tokens, this will
        // throw.
        IRenAsset(getToken()).burn(caller, amountOrTokenId);

        uint256 burnNonce = getEventNonce();

        // If a paylaod of recipient chain has been included, emit more detailed
        // event.
        if (String.isNotEmpty(recipientChain) || recipientPayload.length > 0) {
            emit LogBurnToChain(
                recipientAddress,
                recipientChain,
                recipientPayload,
                amountOrTokenId,
                burnNonce,
                recipientAddress,
                recipientChain
            );
        } else {
            emit LogBurn(bytes(recipientAddress), amountOrTokenId, burnNonce, bytes(recipientAddress));
        }

        _eventNonce = burnNonce + 1;

        return amountOrTokenId;
    }
}

src/Gateways/RenVMSignatureVerifier.sol.content:
// SPDX-License-Identifier: GPL-3.0

// solhint-disable-next-line
pragma solidity ^0.8.0;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ContextUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {EDDSA} from "./EDDSA.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {TransparentUpgradeableProxy} from "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";
import {IERC1271} from "@openzeppelin/contracts/interfaces/IERC1271.sol";

import "hardhat/console.sol";

interface IRenVMSignatureVerifier is IERC1271 {
    // See IERC1271

    function getChain() external view returns (string memory);

    function getMintAuthority() external view returns (bytes memory);
}

contract RenVMSignatureVerifierStateV2 {
    string internal _chain;
    bytes internal _mintAuthority;

    // Leave a gap so that storage values added in future upgrages don't corrupt
    // the storage of contracts that inherit from this contract.
    uint256[48] private __gap;
}

// ERC-1271 uses 4-byte value instead of a boolean so that if a bug causes
// another function to be called (e.g. by proxy misconfiguration or fallbacks),
// a truthy value would not be interpreted as a successful check.
// See https://github.com/ethereum/EIPs/issues/1271#issuecomment-442328339.
bytes4 constant CORRECT_SIGNATURE_RETURN_VALUE_ = 0x1626ba7e;

contract RenVMSignatureVerifierV2 is
    Initializable,
    ContextUpgradeable,
    OwnableUpgradeable,
    RenVMSignatureVerifierStateV2,
    IERC1271,
    IRenVMSignatureVerifier
{
    string public constant NAME = "RenVMSignatureVerifier";

    event LogMintAuthorityUpdated(bytes indexed oldPubKey, bytes indexed newPubKey);

    // bytes4(keccak256("isValidSignature(bytes32,bytes)")
    bytes4 public constant CORRECT_SIGNATURE_RETURN_VALUE = 0x1626ba7e; // CORRECT_SIGNATURE_RETURN_VALUE_
    bytes4 public constant INCORRECT_SIGNATURE_RETURN_VALUE = 0x000000;

    function __RenVMSignatureVerifier_init(
        string calldata chain_,
        uint256 x_,
        uint8 parity_,
        address contractOwner
    ) external initializer {
        __Context_init();
        __Ownable_init();
        _chain = chain_;
        bytes memory mintAuthority = abi.encodePacked(x_, parity_);

        updateMintAuthority(mintAuthority);
        console.log("updated mint authority", _mintAuthority.length);

        if (owner() != contractOwner) {
            transferOwnership(contractOwner);
        }
    }

    function getChain() public view override returns (string memory) {
        return _chain;
    }

    function getMintAuthority() public view override returns (bytes memory) {
        return RenVMSignatureVerifierStateV2._mintAuthority;
    }

    /// @notice Allow the owner to update the mint authority.
    ///
    /// @param _nextMintAuthority The new mint mint authority.
    function updateMintAuthority(bytes memory _nextMintAuthority) public onlyOwner {
        EDDSA.validate(_nextMintAuthority);
        emit LogMintAuthorityUpdated(_mintAuthority, _nextMintAuthority);
        RenVMSignatureVerifierStateV2._mintAuthority = _nextMintAuthority;
    }

    // PUBLIC //////////////////////////////////////////////////////////////////

    /// @notice verifySignature checks the the provided signature matches the
    /// provided parameters. Returns a 4-byte value as defined by ERC1271.
    function isValidSignature(bytes32 sigHash, bytes calldata signature) external view override returns (bytes4) {
        bytes memory mintAuthority_ = getMintAuthority();
        require(mintAuthority_.length != 0, "SignatureVerifier: mintAuthority not initialized");
        if (EDDSA.verify(mintAuthority_, sigHash, signature)) {
            return CORRECT_SIGNATURE_RETURN_VALUE;
        } else {
            return INCORRECT_SIGNATURE_RETURN_VALUE;
        }
    }
}

contract RenVMSignatureVerifierProxy is TransparentUpgradeableProxy {
    constructor(
        address logic,
        address admin,
        bytes memory data
    ) payable TransparentUpgradeableProxy(logic, admin, data) {}
}

src/Gateways/EDDSA.sol.content:
// SPDX-License-Identifier: GPL-3.0
import "hardhat/console.sol";
// solhint-disable-next-line
pragma solidity ^0.8.0;

library EDDSA {
    struct PubKey {
        uint256 x;
        uint8 parity;
    }

    function p() internal pure returns (uint256) {
        return 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEFFFFFC2F;
    }

    function n() internal pure returns (uint256) {
        return 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEBAAEDCE6AF48A03BBFD25E8CD0364141;
    }

    function lessThanP(uint256 _num) internal pure returns (bool) {
        return _num < p();
    }

    function lessThanN(uint256 _num) internal pure returns (bool) {
        return _num < n();
    }

    function validate(bytes memory _pubKey) internal view {
        // PubKey
        uint256 x;
        uint8 parity;

        assembly {
            x := mload(add(_pubKey, 0x20))
            parity := byte(0, mload(add(_pubKey, 0x60)))
        }
        console.log("x", x);
        console.log("parity", parity);

        require(x != 0, "EDDSA: pubKey cannot be set to address zero");
        require(lessThanP(x), "EDDSA: pubKey cannot be greater than p");
        require(parity == 0 || parity == 1, "EDDSA: parity can only be 0 or 1");
    }

    function verify(
        bytes memory pubKey,
        bytes32 sigHash,
        bytes memory signature
    ) internal pure returns (bool) {
        // signature = abi.encode(signature);
        require(signature.length == 96, "EDDSA: Invalid signature length");

        // PubKey
        uint256 x;
        uint8 parity;

        // Signature
        uint256 rx;
        uint256 ry;
        uint256 s;

        assembly {
            x := mload(add(pubKey, 0x20))
            parity := byte(0, mload(add(pubKey, 0x40)))

            rx := mload(add(signature, 0x20))
            ry := mload(add(signature, 0x40))
            s := mload(add(signature, 0x60))
        }

        require(lessThanP(rx), "EDDSA: Invalid RX");
        require(lessThanP(ry), "EDDSA: Invalid RY");
        require(lessThanN(s), "EDDSA: Invalid S");

        uint8 rParity = uint8(ry) & 0x01;

        bytes memory buf = bytes.concat(
            bytes1(rParity),
            abi.encodePacked(rx),
            bytes1(parity),
            abi.encodePacked(x),
            sigHash
        );

        uint256 e = abi.decode(abi.encodePacked(sha256(buf)), (uint256));

        address q = ecrecover(
            bytes32(n() - mulmod(s, x, n())),
            27 + parity,
            bytes32(x),
            bytes32(n() - mulmod(e, x, n()))
        );

        return q == address(uint160(uint256(keccak256(bytes.concat(abi.encodePacked(rx, ry))))));
    }
}

